import React, { useState } from "react";
import { Form, Input, Button, Divider, Select, Layout, Alert, DatePicker, Row, Col } from 'antd';
import Link from "next/link";
import axios from "axios";
import catchErrors from "../utils/catchErrors";
import baseUrl from "../utils/baseUrl";
import { handleLogin } from "../utils/auth";

const { Content } = Layout;
const { Option } = Select;


const INITIAL_USER = {
    email: "",
    password: ""
};

function Infoform() {

    const [user, setUser] = useState(INITIAL_USER);
    const [error, setError] = useState("");

    const [person, setPerson] = useState(2);

    React.useEffect(() => {
        setTimeout(() => {
            setError("");
        }, 5000);
    }, [user]);


    function handleChange(event) {
        const { name, value } = event.target;
        setUser({ ...user, [name]: value });
    }

    function handleDate(date, dateString) {
        console.log(date, dateString);
    }


    function handleSelect(value) {
        console.log(value);
    }


    async function handleFinish() {

        try {
            const url = `${baseUrl}/api/`;
            const payload = { ...user };
            const response = await axios.post(url, payload);
            console.log(response);
            handleLogin(response.data);
        } catch (error) {
            catchErrors(error, setError);
        }

    }

    let errrors = null;
    if (error.length > 0) {
        errrors = <Alert message={error} type="error" showIcon />
    }

    // For Personal Information Form the Second one
    const formItemLayout = {
        labelCol: {
            xs: {
                span: 23,
            },
            sm: {
                span: 4,
            },
        }
    };



    let personForm = null;
    let personNumber = [1,2];


    personForm = 
        <>
            <Layout className="infoform-layout">
                <Content className="content" >
                    <div className="site-layout-background">
                        <h1> For Person 1 </h1>
                        {errrors}
                        <Divider />
                        <Form onFinish={handleFinish} {...formItemLayout}>
                            <Row>
                                <Col span={12}>
                                    <Form.Item name="name" label="Name" rules={[{ required: true, message: 'Please Enter Your Name!' }]} className="infoform-input">
                                        <Input
                                            name="name"
                                            size="large"
                                            placeholder="Name"
                                            onChange={handleChange}
                                        />
                                    </Form.Item>
                                </Col>
                                <Col span={12}>
                                    <Form.Item name="phone" label="Phone" rules={[{ required: true, message: 'Please Enter Your Phone!' }]} className="infoform-input">
                                        <Input
                                            name="phone"
                                            size="large"
                                            placeholder="Phone"
                                            onChange={handleChange}
                                            type="number"
                                        />
                                    </Form.Item>
                                </Col>
                            </Row>


                            <Row>
                                <Col span={12}>
                                    <Form.Item name="address" label="Address" rules={[{ required: true, message: 'Please Enter Your Address!' }]} className="infoform-input">
                                        <Input
                                            name="address"
                                            size="large"
                                            placeholder="Address"
                                            onChange={handleChange}
                                        />
                                    </Form.Item>
                                </Col>
                                <Col span={12}>
                                    <Form.Item name="cnic" label="CNIC" rules={[{ required: true, message: 'Please Enter Your CNIC!' }]} className="infoform-input">
                                        <Input
                                            name="cnic"
                                            size="large"
                                            placeholder="CNIC"
                                            onChange={handleChange}
                                        />
                                    </Form.Item>
                                </Col>
                            </Row>
                        </Form>
                    </div>
                </Content>
            </Layout>
        </>


    return (
        <>
            <Layout className="infoform-layout">
                <Content className="content" >
                    <div className="site-layout-background">
                        <h1> Fill The Form </h1>
                        {errrors}

                        <Divider />

                        <Form onFinish={handleFinish} >

                            <Row className="info1stform-row">
                                <Col span={24} >
                                    {/* <label className="label">Current Location</label> */}
                                    <Select defaultValue="Current Location" size="large" onChange={handleSelect} className="infoform-select">
                                        <Option value="Islamabad">Islamabad</Option>
                                        <Option value="Lahore">Lahore</Option>
                                    </Select>
                                </Col>
                            </Row>
                            <Row className="info1stform-row">
                                <Col span={24}>
                                    {/* <label className="label">Interest</label> */}
                                    <Select defaultValue="Interest" size="large" onChange={handleSelect} className="infoform-select">
                                        <Option value="Islamabad">Islamabad</Option>
                                        <Option value="Lahore">Lahore</Option>
                                    </Select>
                                </Col>
                            </Row>
                            <Row className="info1stform-row">
                                <Col span={24}>
                                    {/* <label className="label">Genre</label> */}
                                    <Select defaultValue="Genre" size="large" onChange={handleSelect} className="infoform-select">
                                        <Option value="Islamabad">Islamabad</Option>
                                        <Option value="Lahore">Lahore</Option>
                                    </Select>
                                </Col>
                            </Row>
                            <Row className="info1stform-row">
                                <Col span={24}>
                                    {/* <label className="label">Number of Person</label> */}
                                    <Select defaultValue="No of Person" size="large" onChange={handleSelect} className="infoform-select">
                                        <Option value="Islamabad">Islamabad</Option>
                                        <Option value="Lahore">Lahore</Option>
                                        <Option value="Lahore">Lahore</Option>
                                    </Select>
                                </Col>
                            </Row>


                            <Row className="info1stform-row">
                                <Col span={24}>
                                    {/* <label className="label">Date</label> */}
                                    <DatePicker onChange={handleDate} size="large" className="infoform-select" />
                                </Col>
                            </Row>

                        </Form>
                    </div>
                </Content>
            </Layout>

            {personForm}



        </>
    );
}

export default Infoform;
