import React, { useState } from "react";
import { Form, Input, Divider, Layout, Alert, Row, Col, Button } from 'antd';
const { Content } = Layout;

const formItemLayout = {
    labelCol: {
        xs: {
            span: 23,
        },
        sm: {
            span: 4,
        },
    }
};



function PersonForm(props) {

    const [error, setError] = useState("");


    // React.useEffect(() => {
    //     console.log(person);
    // }, [person])


    function handleChange(event) {

    }


    function handleFinish(id) {

    }


    let message = null;
    if (error.length > 0) {
        message = <Alert message={error} type="error" showIcon />
    }


    return <>

        <div className="form">
            <div id="sendmessage">{message}</div>
            <div id="errormessage">{message}</div>
            <Form onFinish={handleFinish} className="contactForm">
                <div className="form-row">
                    <div className="form-group col-md-6">
                        <Form.Item name="name" rules={[{ required: true, message: 'Please Enter Your Name!' }]} className="form-control">
                            <Input
                                name="name"
                                size="large"
                                placeholder="Name"
                                onChange={handleChange}
                            />
                        </Form.Item>
                    </div>
                    <div className="form-group col-md-6">
                        <input type="email" className="form-control" name="email" id="email" placeholder="Your Email" data-rule="email" data-msg="Please enter a valid email" />
                        <div className="validation"></div>
                    </div>
                </div>
                <div className="form-group">
                    <input type="text" className="form-control" name="subject" id="subject" placeholder="Subject" data-rule="minlen:4" data-msg="Please enter at least 8 chars of subject" />
                    <div className="validation"></div>
                </div>
                <div className="form-group">
                    <textarea className="form-control" name="message" rows="5" data-rule="required" data-msg="Please write something for us" placeholder="Message"></textarea>
                    <div className="validation"></div>
                </div>
                <div className="text-center"><button type="submit">Send Message</button></div>
            </Form>
        </div>







        {/* <Form.Item name="phone" label="Phone" rules={[{ required: true, message: 'Please Enter Your Phone!' }]} className="infoform-input">
                <Input
                    name="phone"
                    size="large"
                    placeholder="Phone"
                    onChange={handleChange}
                    type="number"
                />
            </Form.Item>


            <Form.Item name="address" label="Address" rules={[{ required: true, message: 'Please Enter Your Address!' }]} className="infoform-input">
                <Input
                    name="address"
                    size="large"
                    placeholder="Address"
                    onChange={handleChange}
                />
            </Form.Item>

            <Form.Item name="cnic" label="CNIC" rules={[{ required: true, message: 'Please Enter Your CNIC!' }]} className="infoform-input">
                <Input
                    name="cnic"
                    size="large"
                    placeholder="CNIC"
                    onChange={handleChange}
                />
            </Form.Item> */}
    </>

}

export default PersonForm;
