import React, { useState } from "react";
import { Form, Input, Button, Divider, Select, Layout, Alert, DatePicker, Row, Col } from 'antd';
import Link from "next/link";
import axios from "axios";
import catchErrors from "../utils/catchErrors";
import baseUrl from "../utils/baseUrl";
import { handleLogin } from "../utils/auth";

const { Content } = Layout;
const { Option } = Select;


const INITIAL_USER = {
    email: "",
    password: ""
};

function Infoform() {

    const [user, setUser] = useState(INITIAL_USER);
    const [error, setError] = useState("");


    React.useEffect(() => {
        setTimeout(() => {
            setError("");
        }, 5000);
    }, [user]);


    function handleChange(event) {
        const { name, value } = event.target;
        setUser({ ...user, [name]: value });
    }

    function handleDate(date, dateString) {
        console.log(date, dateString);
    }


    async function handleFinish() {

        try {
            const url = `${baseUrl}/api/`;
            const payload = { ...user };
            const response = await axios.post(url, payload);
            console.log(response);
            handleLogin(response.data);
        } catch (error) {
            catchErrors(error, setError);
        }

    }

    let errrors = null;
    if (error.length > 0) {
        errrors = <Alert message={error} type="error" showIcon />
    }

    // For Personal Information Form the Second one
    const formItemLayout = {
        labelCol: {
            xs: {
                span: 23,
            },
            sm: {
                span: 4,
            },
        }
    };


    return (
        <>
            <Layout className="infoform-layout">
                <Content className="content" >
                    <div className="site-layout-background">
                        <h1> Fill The Form </h1>
                        {errrors}

                        <Divider />

                        <Form onFinish={handleFinish} {...formItemLayout}>

                            <label className="label">Current Location</label>
                            <Select defaultValue="Please Select Your Current Location" size="large" onChange={handleChange} >
                                <Option value="Islamabad">Islamabad</Option>
                                <Option value="Lahore">Lahore</Option>
                            </Select>

                            <Divider />

                            <label className="label">Interest</label>
                            <Select defaultValue="Please Select Your Interest" size="large" onChange={handleChange} >
                                <Option value="Islamabad">Islamabad</Option>
                                <Option value="Lahore">Lahore</Option>
                            </Select>

                            <Divider />

                            <label className="label">Genre</label>
                            <Select defaultValue="Please Select Your Genre" size="large" onChange={handleChange} >
                                <Option value="Islamabad">Islamabad</Option>
                                <Option value="Lahore">Lahore</Option>
                            </Select>

                            <Divider />

                            <label className="label">Number of Person</label>
                            <Select defaultValue="Please Select Number of Person" size="large" onChange={handleChange}>
                                <Option value="Islamabad">Islamabad</Option>
                                <Option value="Lahore">Lahore</Option>
                                <Option value="Lahore">Lahore</Option>
                            </Select>

                            <Divider />

                            <label className="label">Date</label>
                            <DatePicker onChange={handleDate} />
                        </Form>
                    </div>
                </Content>
            </Layout>

            <Layout className="infoform-layout">
                <Content className="content" >
                    <div className="site-layout-background" {...formItemLayout}>
                        <Form onFinish={handleFinish} {...formItemLayout}>
                            <Row>
                                <Col span={12}>
                                    <Form.Item name="name" label="Name" rules={[{ required: true, message: 'Please Enter Your Name!' }]} className="infoform-input">
                                        <Input
                                            name="name"
                                            size="large"
                                            placeholder="Name"
                                            onChange={handleChange}
                                        />
                                    </Form.Item>
                                </Col>
                                <Col span={12}>
                                    <Form.Item name="phone" label="Phone" rules={[{ required: true, message: 'Please Enter Your Phone!' }]} className="infoform-input">
                                        <Input
                                            name="phone"
                                            size="large"
                                            placeholder="Phone"
                                            onChange={handleChange}
                                            type="number"
                                        />
                                    </Form.Item>
                                </Col>
                            </Row>


                            <Row>
                                <Col span={12}>
                                    <Form.Item name="address" label="Address" rules={[{ required: true, message: 'Please Enter Your Address!' }]} className="infoform-input">
                                        <Input
                                            name="address"
                                            size="large"
                                            placeholder="Address"
                                            onChange={handleChange}
                                        />
                                    </Form.Item>
                                </Col>
                                <Col span={12}>
                                    <Form.Item name="cnic" label="CNIC" rules={[{ required: true, message: 'Please Enter Your CNIC!' }]} className="infoform-input">
                                        <Input
                                            name="cnic"
                                            size="large"
                                            placeholder="CNIC"
                                            onChange={handleChange}
                                        />
                                    </Form.Item>
                                </Col>
                            </Row>
                        </Form>
                    </div>
                </Content>
            </Layout>
        </>
    );
}

export default Infoform;
