import React, { useState } from "react";
import { Form, Divider, Select, Layout, Alert, DatePicker, Row, Col } from 'antd';
import axios from "axios";
import catchErrors from "../utils/catchErrors";
import baseUrl from "../utils/baseUrl";
import { handleLogin } from "../utils/auth";
import PersonForm from '../components/Form/PersonForm'

const { Content } = Layout;
const { Option } = Select;

const GENRAL_DATA = {
    location: "",
    interest: "",
    genre: "",
    person: ""
};

const INITIAL_PERSON = {
    name: "",
    phone: "",
    address: "",
    cnic: ""
};

function Infoform() {

    const [data, setData] = useState(GENRAL_DATA);
    const [error, setError] = useState("");
    const [person, setPerson] = useState(INITIAL_PERSON);
    // because two person are mandatory
    const [personList, setPersonList] = useState([INITIAL_PERSON, INITIAL_PERSON]);
    function handleDate(date, dateString) {
        console.log(date, dateString);
    }

    function handleSelect(event) {
        const { id, value } = event.target;
        setData({ ...data, [name]: value });
    }

    async function handleFinish() {
        try {
            const url = `${baseUrl}/api/`;
            const payload = { ...user };
            const response = await axios.post(url, payload);
            console.log(response);
            handleLogin(response.data);
        } catch (error) {
            catchErrors(error, setError);
        }
    }

    let errrors = null;
    if (error.length > 0) {
        errrors = <Alert message={error} type="error" showIcon />
    }

    let personForm = null;

    personForm = personList.map((person, index) => {
        return <PersonForm Number={index + 1} list={personList} />
    });


    return (
        <>
            <Layout className="infoform-layout">
                <Content className="content" >
                    <div className="site-layout-background">
                        <h1> Fill The Form </h1>
                        {errrors}

                        <Divider />
                        <Form onFinish={handleFinish} >

                            <Row className="info1stform-row">
                                <Col span={24} >
                                    {/* <label className="label">Current Location</label> */}
                                    <Select defaultValue="Current Location" size="large" onChange={handleSelect} className="infoform-select" id="location">
                                        <Option value="Islamabad">Islamabad</Option>
                                        <Option value="Lahore">Lahore</Option>
                                    </Select>
                                </Col>
                            </Row>
                            <Row className="info1stform-row">
                                <Col span={24}>
                                    {/* <label className="label">Interest</label> */}
                                    <Select defaultValue="Interest" size="large" onChange={handleSelect} className="infoform-select" id="interest">
                                        <Option value="Islamabad">Islamabad</Option>
                                        <Option value="Lahore">Lahore</Option>
                                    </Select>
                                </Col>
                            </Row>
                            <Row className="info1stform-row">
                                <Col span={24}>
                                    {/* <label className="label">Genre</label> */}
                                    <Select defaultValue="Genre" size="large" onChange={handleSelect} className="infoform-select" id="genre">
                                        <Option value="Islamabad">Islamabad</Option>
                                        <Option value="Lahore">Lahore</Option>
                                    </Select>
                                </Col>
                            </Row>
                            <Row className="info1stform-row">
                                <Col span={24}>
                                    {/* <label className="label">Number of Person</label> */}
                                    <Select defaultValue="No of Person" size="large" onChange={handleSelect} className="infoform-select" id="person">
                                        <Option value="Islamabad">Islamabad</Option>
                                        <Option value="Lahore">Lahore</Option>
                                        <Option value="Lahore">Lahore</Option>
                                    </Select>
                                </Col>
                            </Row>


                            <Row className="info1stform-row">
                                <Col span={24}>
                                    {/* <label className="label">Date</label> */}
                                    <DatePicker onChange={handleDate} size="large" className="infoform-select" />
                                </Col>
                            </Row>

                        </Form>
                    </div>
                </Content>
            </Layout>

            {personForm}

        </>
    );
}

export default Infoform;
