import React, { useState } from "react";
import { Form, Input, Button, Divider, Select, Layout, Alert } from 'antd';
import Link from "next/link";
import axios from "axios";
import catchErrors from "../utils/catchErrors";
import baseUrl from "../utils/baseUrl";
import { handleLogin } from "../utils/auth";

const { Content } = Layout;
const { Option } = Select;


const INITIAL_USER = {
    email: "",
    password: ""
};

function Infoform() {

    const [user, setUser] = useState(INITIAL_USER);
    const [error, setError] = useState("");


    React.useEffect(() => {
        setTimeout(() => {
            setError("");
        }, 5000);
    }, [user]);


    function handleChange(event) {
        const { name, value } = event.target;
        setUser({ ...user, [name]: value });
    }

    async function handleFinish() {

        try {
            const url = `${baseUrl}/api/login`;
            const payload = { ...user };
            const response = await axios.post(url, payload);
            console.log(response);
            handleLogin(response.data);
        } catch (error) {
            catchErrors(error, setError);
        }

    }

    let errrors = null;
    if (error.length > 0) {
        errrors = <Alert message={error} type="error" showIcon />
    }


    return (
        <>
            <Layout className="layout">
                <Content className="content" >
                    <div className="site-layout-background">
                        <h1> Login </h1>
                        {errrors}

                        <Divider />

                        <Form onFinish={handleFinish}>

    
                            <label className="label">Current Location</label>
                            <Select defaultValue="Please Select Your Current Location" label="Current Location" size="large" onChange={handleChange} className="input">
                                <Option value="Islamabad">Islamabad</Option>
                                <Option value="lahore">Lahore</Option>
                            </Select>



                        </Form>
                    </div>
                </Content>
            </Layout>
        </>
    );
}

export default Infoform;
