import React, { useState } from "react";
import { Form, Input, Button, Divider, Select, Layout, Alert, DatePicker } from 'antd';
import Link from "next/link";
import axios from "axios";
import catchErrors from "../utils/catchErrors";
import baseUrl from "../utils/baseUrl";
import { handleLogin } from "../utils/auth";

const { Content } = Layout;
const { Option } = Select;


const INITIAL_USER = {
    email: "",
    password: ""
};

function Infoform() {

    const [user, setUser] = useState(INITIAL_USER);
    const [error, setError] = useState("");


    React.useEffect(() => {
        setTimeout(() => {
            setError("");
        }, 5000);
    }, [user]);


    function handleChange(event) {
        const { name, value } = event.target;
        setUser({ ...user, [name]: value });
    }

    function handleDate(date, dateString) {
        console.log(date, dateString);
    }


    async function handleFinish() {

        try {
            const url = `${baseUrl}/api/`;
            const payload = { ...user };
            const response = await axios.post(url, payload);
            console.log(response);
            handleLogin(response.data);
        } catch (error) {
            catchErrors(error, setError);
        }

    }

    let errrors = null;
    if (error.length > 0) {
        errrors = <Alert message={error} type="error" showIcon />
    }


    return (
        <>
            <Layout className="infoform-layout">
                <Content className="content" >
                    <div className="site-layout-background">
                        <h1> Fill The Form </h1>
                        {errrors}

                        <Divider />

                        <Form onFinish={handleFinish}>

                            <label className="label">Current Location</label>
                            <Select defaultValue="Please Select Your Current Location" size="large" onChange={handleChange} >
                                <Option value="Islamabad">Islamabad</Option>
                                <Option value="Lahore">Lahore</Option>
                            </Select>

                            <label className="label">Interest</label>
                            <Select defaultValue="Please Select Your Interest" size="large" onChange={handleChange} >
                                <Option value="Islamabad">Islamabad</Option>
                                <Option value="Lahore">Lahore</Option>
                            </Select>

                            <label className="label">Genre</label>
                            <Select defaultValue="Please Select Your Genre" size="large" onChange={handleChange} >
                                <Option value="Islamabad">Islamabad</Option>
                                <Option value="Lahore">Lahore</Option>
                            </Select>

                            <label className="label">Number of Person</label>
                            <Select defaultValue="Please Select Number of Person" size="large" onChange={handleChange}>
                                <Option value="Islamabad">Islamabad</Option>
                                <Option value="Lahore">Lahore</Option>
                                <Option value="Lahore">Lahore</Option>
                            </Select>

                            <label className="label">Date</label>
                            <DatePicker onChange={handleDate} />

                            <label className="label">Name</label>
                            <Form.Item name="name" rules={[{ required: true, message: 'Please Enter Your Name!' }]}>
                                <Input
                                    name="name"
                                    size="large"
                                    placeholder="Name"
                                    onChange={handleChange}
                                />
                            </Form.Item>

                            <label className="label">Phone</label>
                            <Form.Item name="phone" rules={[{ required: true, message: 'Please Enter Your Phone!' }]}>
                                <Input
                                    name="phone"
                                    size="large"
                                    placeholder="Phone"
                                    onChange={handleChange}
                                    type="number"
                                />
                            </Form.Item>

                            <label className="label">Address</label>
                            <Form.Item name="address" rules={[{ required: true, message: 'Please Enter Your Address!' }]}>
                                <Input
                                    name="address"
                                    size="large"
                                    placeholder="Address"
                                    onChange={handleChange}
                                />
                            </Form.Item>

                            <label className="label">CNIC</label>
                            <Form.Item name="cnic" rules={[{ required: true, message: 'Please Enter Your CNIC!' }]}>
                                <Input
                                    name="cnic"
                                    size="large"
                                    placeholder="CNIC"
                                    onChange={handleChange}
                                />
                            </Form.Item>

                        </Form>
                    </div>
                </Content>
            </Layout>
        </>
    );
}

export default Infoform;
