import React, { useState } from "react";
import { Form, Input, Button, Divider, Layout, Alert, InputNumber } from 'antd';
import { UserOutlined, LockOutlined, FacebookFilled, GooglePlusSquareFilled, } from '@ant-design/icons';
import Link from "next/link";
import axios from "axios";
import catchErrors from "../utils/catchErrors";
import baseUrl from "../utils/baseUrl";

const { Content } = Layout;

const INITIAL_USER = {
  email: "",
  password: ""
};

function Signup() {

  const [user, setUser] = useState(INITIAL_USER);
  const [error, setError] = useState("");


  React.useEffect(() => {
    // setError(preverr => preverr.concat('Samee'));
  }, [user]);


  function handleChange(event) {
    const { name, value } = event.target;
    setUser({ ...user, [name]: value });
  }

  function handleFinish(event) {
    setError("Hey");
    error.map(err => {
      return console.log(err);
    })
    console.log(user);
    try {
      const url = `${baseUrl}/api/signup`;
      const payload = { ...user };

    } catch (error) {
      catchErrors(error, setError);
    }
  }



  let errrors = null;
  if (error.length > 0) {
    errrors = <Alert message={error} type="error" showIcon />
  }


  return (
    <>
      <Layout className="layout">
        <Content className="content" >
          <div className="site-layout-background">
            <h1> Login </h1>
            {errrors}

            <Divider />

            <Form onFinish={handleFinish}>

            <Form.Item name="name" label="Name" rules={[{ required: true, message: 'Please Enter Your Name!' }]}>
                <Input
                  name="name"
                  className="input"
                  size="large"
                  placeholder="Name"
                  onChange={handleChange}
                />
              </Form.Item>

              <Form.Item name="phone" label="Phone" rules={[{ type: 'number', min: 18, max: 99, message: 'Please Enter Your Phone!' }]}>
                <InputNumber 
                 name="phone"
                 className="input"
                 size="large"
                 placeholder="Phone"
                 onChange={handleChange}
                />
              </Form.Item>

              <Form.Item name="email" label="Email" rules={[{ required: true, message: 'Please Enter Your Email!' }]}>
                <Input
                  name="email"
                  className="input"
                  size="large"
                  placeholder="Email"
                  onChange={handleChange}
                  type="email"
                />
              </Form.Item>

              <Form.Item name="password" label="Password" rules={[{ required: true, message: 'Please Enter Your Password!' }]}>
                <Input
                  name="password"
                  className="input"
                  size="large"
                  type="password"
                  placeholder="Password"
                  onChange={handleChange}
                />
              </Form.Item>

              <Form.Item name="confirmpassword"  label="Confirm Password" rules={[{ required: true, message: 'Please Enter Your Password Agian!' }]}>
                <Input
                  name="confirm password"
                  className="input"
                  size="large"
                  type="password"
                  placeholder="Confirm Password"
                  onChange={handleChange}
                />
              </Form.Item>

              <Form.Item >
                <Button type="primary" htmlType="submit" shape="round" size="large">
                  Register
                </Button>
              </Form.Item>

            </Form>

          </div>
        </Content>
      </Layout>
    </>
  );
}

export default Signup;
