import { Card } from 'semantic-ui-react';



function PlacesSummary({ placesdata }) {

    function mapPlaces(placesdata) {
        return placesdata.map(place => ({
            header: place.name,
            image: place.picture,
            color: 'teal',
            fluid: true,
            childkey: place._id,
            href: `/product?_id=${place._id}`
        }))
    }

    return <Card.Group itemsPerRow="3" stackable centered items={mapPlaces(placesdata)} />;
}

export default PlacesSummary;
