import React, { useState } from "react";

const INITIAL_PERSON = {
    name: "",
    phone: "",
    address: "",
    cnic: ""
};

// For Personal Information Form the Second one
const formItemLayout = {
    labelCol: {
        xs: {
            span: 23,
        },
        sm: {
            span: 4,
        },
    }
};


function PersonForm(props) {

    const [error, setError] = useState("");
    const [person, setPerson] = useState(INITIAL_PERSON);
    // because two person are mandatory
    const [personList, setPersonList] = useState([INITIAL_PERSON, INITIAL_PERSON]);


    function handleChange(event) {
        const { name, value } = event.target;
        setPerson({ ...person, [name]: value });
    }


    function handleFinish() {

    }

    let errrors = null;
    if (error.length > 0) {
        errrors = <Alert message={error} type="error" showIcon />
    }
    
    return <>
        <Layout className="infoform-layout">
            <Content className="content" >
                <div className="site-layout-background">
                    <h1> For Person {props.Number} </h1>
                    {errrors}
                    <Divider />
                    <Form onFinish={handleFinish} {...formItemLayout}>
                        <Row>
                            <Col span={12}>
                                <Form.Item name="name" label="Name" rules={[{ required: true, message: 'Please Enter Your Name!' }]} className="infoform-input">
                                    <Input
                                        name="name"
                                        size="large"
                                        placeholder="Name"
                                        onChange={handleChange}
                                    />
                                </Form.Item>
                            </Col>
                            <Col span={12}>
                                <Form.Item name="phone" label="Phone" rules={[{ required: true, message: 'Please Enter Your Phone!' }]} className="infoform-input">
                                    <Input
                                        name="phone"
                                        size="large"
                                        placeholder="Phone"
                                        onChange={handleChange}
                                        type="number"
                                    />
                                </Form.Item>
                            </Col>
                        </Row>


                        <Row>
                            <Col span={12}>
                                <Form.Item name="address" label="Address" rules={[{ required: true, message: 'Please Enter Your Address!' }]} className="infoform-input">
                                    <Input
                                        name="address"
                                        size="large"
                                        placeholder="Address"
                                        onChange={handleChange}
                                    />
                                </Form.Item>
                            </Col>
                            <Col span={12}>
                                <Form.Item name="cnic" label="CNIC" rules={[{ required: true, message: 'Please Enter Your CNIC!' }]} className="infoform-input">
                                    <Input
                                        name="cnic"
                                        size="large"
                                        placeholder="CNIC"
                                        onChange={handleChange}
                                    />
                                </Form.Item>
                            </Col>
                        </Row>
                    </Form>
                </div>
            </Content>
        </Layout>
    </>

}

export default PersonForm;
