import React, { useState } from "react";
import { Form, Input, Button, Divider, Layout, Alert } from 'antd';
import { UserOutlined, LockOutlined, FacebookFilled, GooglePlusSquareFilled, } from '@ant-design/icons';
import Link from "next/link";
import axios from "axios";
import catchErrors from "../utils/catchErrors";
import baseUrl from "../utils/baseUrl";

const { Content } = Layout;

const INITIAL_USER = {
  email: "",
  password: ""
};

const INITIAL_ERROR = [];

function Login() {
  const [user, setUser] = useState(INITIAL_USER);
  const [error, setError] = React.useState(false);

  function handleChange(event) {
    const { name, value } = event.target;
    setUser({ ...user, [name]: value });
  }

  function handleFinish(event) {
    console.log(user);
    try {
      setError("");
      const url = `${baseUrl}/api/login`;
      const payload = { ...user };

    } catch (error) {
      catchErrors(error, setError);
    }
  }

  return (
    <>
      <Layout className="layout">
        <Content className="content" >
          <div className="site-layout-background">
            <h1> Login </h1>
            <br />

            {error ? <Alert
              message="Error"
              type="error"
              showIcon
            /> : <> </>
            }
            <Divider />

            <Form onFinish={handleFinish}>
              <Form.Item name="username">
                <Input
                  name="email"
                  className="input"
                  size="large"
                  prefix={<UserOutlined />}
                  placeholder="Username"
                  onChange={handleChange}
                />
              </Form.Item>

              <Form.Item name="password">
                <Input
                  name="password"
                  className="input"
                  size="large"
                  prefix={<LockOutlined />}
                  type="password"
                  placeholder="Password"
                  onChange={handleChange}
                />
              </Form.Item>

              <Form.Item>
                <a href="">
                  Forgot password
                </a>
              </Form.Item>

              <Form.Item >
                <Button type="primary" htmlType="submit" shape="round" size="large">
                  Log in
                </Button>
              </Form.Item>

            </Form>

            <Button shape="round" icon={<FacebookFilled />} size="large">
              Sign in with Facebook
            </Button>
            <Divider style={{ margin: "2px" }} />
            <Button shape="round" icon={<GooglePlusSquareFilled />} size="large">
              Sign in with Google
            </Button>

          </div>
        </Content>
      </Layout>
    </>
  );
}

export default Login;
